import React, {useState} from "react";

export default function PostItem(props){
    const [like, setLike] = useState(0)
    const [comentario, setComentario]  = useState('')
    const [comentado, setComentado] = useState('')
    
    function darLike(){
        setLike(like + 1)
    }

    function comentar(){
        setComentado(comentario)
        limpar()
    }

    function limpar(){
        setComentario("")
    }
  
    return(
        <div className="post">
            <h2>{props.titulo}</h2>
            <h3>{like}</h3>
            <p>{props.post}</p>
            <button onClick={darLike}>Like</button>
            <br/>
            <input value={comentario} onChange={ event => setComentario(event.target.value) } type='text' placeholder='comentario'/>
            <button onClick={comentar}>Comentar</button>
            <p>{comentado}</p>
        </div>
    );
}
